import React, { useState, useEffect } from "react";
import {
  FaFacebook,
  FaInstagram,
  FaYoutube,
  FaChevronDown,
  FaChevronUp,
  FaArrowUp,
} from "react-icons/fa";
import { useTranslation } from "react-i18next";

const Footer = () => {
  const { t } = useTranslation();
  const [openDropdown, setOpenDropdown] = useState(null);
  const [showScrollTop, setShowScrollTop] = useState(false);
  const [email, setEmail] = useState("");
  const [submitted, setSubmitted] = useState(false);
  const [modalImage, setModalImage] = useState(null);
  const [modalCaption, setModalCaption] = useState("");

  const toggleDropdown = (item) => {
    setOpenDropdown(openDropdown === item ? null : item);
  };

  useEffect(() => {
    const handleScroll = () => {
      setShowScrollTop(window.scrollY > 100);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  const handleSubscribe = (e) => {
    e.preventDefault();
    if (email.includes("@") && email.includes(".")) {
      setSubmitted(true);
      setEmail("");
      setTimeout(() => setSubmitted(false), 3000);
    }
  };

  const dropdownItems = {
    follow: [
      { icon: <FaFacebook />, text: "Facebook", href: "https://facebook.com" },
      {
        icon: <FaInstagram />,
        text: "Instagram",
        href: "https://instagram.com",
      },
      { icon: <FaYoutube />, text: "YouTube", href: "https://youtube.com" },
    ],
  };

  const instagramCaptions = [
    "Harvest Day in Ghana",
    "Shea Butter in the Making",
    "Packing with Love",
    "Nordic Wellness Showcase",
    "Eco Soap Behind the Scenes",
    "Community Farmers Market",
  ];

  return (
    <footer className="bg-[#146b39] text-gold-200 pt-12 pb-8 px-4 md:px-8 relative">
      <div className="max-w-7xl mx-auto">
        {/* Newsletter Section */}
        <div className="mb-12">
          <h3 className="text-xl font-semibold mb-2 text-gold-400">
            {t("footer.newsletter")}
          </h3>
          <p className="mb-4 text-gold-300">{t("footer.newsletterDesc")}</p>
          <form onSubmit={handleSubscribe} className="max-w-md">
            <div className="flex flex-col sm:flex-row gap-2">
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder={t("footer.emailPlaceholder")}
                className="px-4 py-2 flex-grow rounded text-gray-800 focus:outline-none focus:ring-2 focus:ring-gold-400"
                aria-label="Email address"
                required
              />
              <button
                type="submit"
                className="bg-gold-500 hover:bg-gold-600 text-[#146b39] font-medium px-4 py-2 rounded transition whitespace-nowrap"
              >
                {t("footer.subscribe")}
              </button>
            </div>
            {submitted && (
              <p className="text-gold-200 mt-2 text-sm animate-fade-in">
                {t("footer.thankYou")}
              </p>
            )}
          </form>
        </div>

        {/* Instagram Gallery */}
        <div className="mb-10">
          <h3 className="text-xl font-semibold mb-4 text-gold-400">
            {t("footer.instagramGallery")}
          </h3>
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-6 gap-3">
            {[1, 2, 3, 4, 5, 6].map((i) => (
              <div
                key={i}
                className="group cursor-pointer"
                onClick={() => {
                  setModalImage(`/images/instagram${i}.jpg`);
                  setModalCaption(instagramCaptions[i - 1]);
                }}
              >
                <div className="aspect-square overflow-hidden rounded-lg shadow-md mb-1">
                  <img
                    src={`/images/instagram${i}.jpg`}
                    alt={`Instagram preview ${i}`}
                    className="w-full h-full object-cover transition-transform duration-300 group-hover:scale-105"
                    loading="lazy"
                  />
                </div>
                <p className="text-xs text-gold-300 text-center truncate">
                  {t(`footer.instagramTag${i}`)}
                </p>
              </div>
            ))}
          </div>
          <div className="mt-4 text-center">
            <a
              href="https://instagram.com"
              target="_blank"
              rel="noopener noreferrer"
              className="inline-flex items-center gap-2 text-gold-300 hover:text-gold-100 transition"
            >
              <FaInstagram className="text-lg" />
              {t("footer.viewOnInstagram")}
            </a>
          </div>
        </div>

        {/* Footer Links */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8 mb-8">
          {/* Follow Us */}
          <div>
            <h3
              onClick={() => toggleDropdown("follow")}
              className="flex items-center gap-2 text-xl font-semibold mb-4 cursor-pointer text-gold-400 hover:text-gold-300 transition"
            >
              <span>{t("footer.followUs")}</span>
              {openDropdown === "follow" ? <FaChevronUp /> : <FaChevronDown />}
            </h3>
            {openDropdown === "follow" && (
              <ul className="space-y-3">
                {dropdownItems.follow.map((item, index) => (
                  <li
                    key={index}
                    className="flex items-center gap-3 hover:text-gold-100 transition"
                  >
                    <span className="text-gold-400">{item.icon}</span>
                    <a
                      href={item.href}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-gold-300 hover:text-gold-100"
                    >
                      {item.text}
                    </a>
                  </li>
                ))}
              </ul>
            )}
          </div>

          {/* Quick Links */}
          <div>
            <h3 className="text-xl font-semibold mb-4 text-gold-400">
              {t("footer.quickLinks")}
            </h3>
            <ul className="space-y-3">
              {["support", "suggestions", "testimonials", "privacy"].map(
                (item) => (
                  <li key={item}>
                    <a
                      href={`/${item}`}
                      className="text-gold-300 hover:text-gold-100 transition"
                    >
                      {t(`footer.${item}`)}
                    </a>
                  </li>
                )
              )}
            </ul>
          </div>
        </div>

        {/* Copyright */}
        <div className="border-t border-gold-700 pt-6 flex flex-col md:flex-row justify-between items-center gap-4">
          <p className="text-gold-300 text-sm">
            © {new Date().getFullYear()} Trophford. {t("footer.rightsReserved")}
          </p>
          <div className="flex flex-wrap justify-center gap-4">
            {["terms", "shipping", "returns"].map((item) => (
              <a
                key={item}
                href={`/${item}`}
                className="text-gold-300 hover:text-gold-100 text-sm transition"
              >
                {t(`footer.${item}`)}
              </a>
            ))}
          </div>
        </div>

        {/* Scroll to Top Button */}
        {showScrollTop && (
          <button
            onClick={scrollToTop}
            className="fixed bottom-6 right-6 bg-gold-500 text-[#146b39] p-3 rounded-full shadow-lg hover:bg-gold-400 transition-transform transform hover:scale-110 z-50"
            aria-label="Scroll to top"
          >
            <FaArrowUp />
          </button>
        )}

        {/* Instagram Modal */}
        {modalImage && (
          <div
            className="fixed inset-0 bg-black bg-opacity-90 flex items-center justify-center z-50 p-4 cursor-pointer"
            onClick={() => setModalImage(null)}
          >
            <div className="max-w-3xl w-full">
              <img
                src={modalImage}
                alt="Instagram full view"
                className="w-full h-auto max-h-[80vh] object-contain rounded-lg"
              />
              <p className="text-gold-300 text-center mt-4">{modalCaption}</p>
            </div>
          </div>
        )}
      </div>
    </footer>
  );
};

export default Footer;
