import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";
import { useTranslation } from "react-i18next";
import LanguageSwitcher from "./LanguageSwitcher";
import ThemeToggle from "./ThemeToggle";
import {
  FaHome,
  FaConciergeBell,
  FaCompass,
  FaEnvelope,
  FaInfoCircle,
  FaBars,
  FaTimes,
} from "react-icons/fa";

export default function Header() {
  const location = useLocation();
  const { t } = useTranslation();
  const [menuOpen, setMenuOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const [showTagline, setShowTagline] = useState(true);

  useEffect(() => {
    const onScroll = () => {
      setScrolled(window.scrollY > 10);
      setShowTagline(window.scrollY < 60);
    };
    window.addEventListener("scroll", onScroll);
    return () => window.removeEventListener("scroll", onScroll);
  }, []);

  const navLinkClasses = (path) =>
    `relative flex items-center gap-2 px-4 py-2 rounded-md transition-all duration-300 font-medium text-sm group
     ${
       location.pathname === path
         ? "bg-green-600 text-white"
         : "text-gray-700 dark:text-gray-200 hover:bg-green-100 dark:hover:bg-green-800 hover:text-green-800 dark:hover:text-white"
     }`;

  const toggleMenu = () => setMenuOpen(!menuOpen);

  return (
    <header
      className={`sticky top-0 z-50 w-full transition-all duration-300 ${
        scrolled
          ? "bg-white/95 dark:bg-gray-900/95 shadow-md backdrop-blur-md"
          : "bg-white dark:bg-gray-900"
      } border-b border-gray-100 dark:border-gray-800`}
    >
      <div className="max-w-screen-2xl mx-auto px-4 md:px-6 py-4">
        <div className="flex items-center justify-between">
          {/* Logo - Left aligned */}
          <div className="flex items-center">
            <Link
              to="/"
              className="hover:opacity-90 transition-opacity flex items-center gap-2"
            >
              <img
                src="/images/tropinordlogo001.png"
                alt="TropiNord Logo"
                className="h-10 w-auto object-contain" // Reduced height for compact header
              />
              <span
                className={`hidden sm:block text-xs md:text-sm font-light transition-all duration-500 ease-in-out transform ${
                  showTagline
                    ? "opacity-100 translate-y-0"
                    : "opacity-0 -translate-y-2"
                } text-gray-600 dark:text-gray-300`}
              >
                Tropical Traditions, Nordic Standards.
              </span>
            </Link>
          </div>

          {/* Navigation and tools - Right aligned */}
          <div className="flex items-center gap-4">
            {/* Hamburger Menu - Mobile only */}
            <button
              className="md:hidden text-gray-700 dark:text-gray-200 text-2xl"
              onClick={toggleMenu}
              aria-label="Toggle navigation menu"
            >
              {menuOpen ? <FaTimes /> : <FaBars />}
            </button>

            {/* Desktop Navigation */}
            <nav className="hidden md:flex items-center gap-3">
              <Link to="/" className={navLinkClasses("/")}>
                <FaHome className="text-xs" />
                {t("nav.home")}
              </Link>
              <Link to="/services" className={navLinkClasses("/services")}>
                <FaConciergeBell className="text-xs" />
                {t("nav.services")}
              </Link>
              <Link to="/explore" className={navLinkClasses("/explore")}>
                <FaCompass className="text-xs" />
                {t("nav.explore")}
              </Link>
              <Link to="/contact" className={navLinkClasses("/contact")}>
                <FaEnvelope className="text-xs" />
                {t("nav.contact")}
              </Link>
              <Link to="/about" className={navLinkClasses("/about")}>
                <FaInfoCircle className="text-xs" />
                About
              </Link>
            </nav>

            {/* Order Button - Desktop only */}
            <Link
              to="/order"
              className="hidden md:block bg-green-600 hover:bg-green-700 text-white font-semibold px-4 py-2 rounded-md shadow transition duration-200"
            >
              🌍 Place Order
            </Link>

            {/* Tools */}
            <div className="hidden md:flex items-center gap-4">
              <ThemeToggle />
              <LanguageSwitcher />
            </div>
          </div>
          </div>

          {/* Mobile Menu */}
          <nav
            className={`w-full flex-col gap-2 mt-4 ${
              menuOpen ? "flex animate-slide-down" : "hidden"
            }`}
          >
            <Link
              to="/"
              className={navLinkClasses("/")}
              onClick={() => setMenuOpen(false)}
            >
              <FaHome className="text-xs" />
              {t("nav.home")}
            </Link>
            <Link
              to="/services"
              className={navLinkClasses("/services")}
              onClick={() => setMenuOpen(false)}
            >
              <FaConciergeBell className="text-xs" />
              {t("nav.services")}
            </Link>
            <Link
              to="/explore"
              className={navLinkClasses("/explore")}
              onClick={() => setMenuOpen(false)}
            >
              <FaCompass className="text-xs" />
              {t("nav.explore")}
            </Link>
            <Link
              to="/contact"
              className={navLinkClasses("/contact")}
              onClick={() => setMenuOpen(false)}
            >
              <FaEnvelope className="text-xs" />
              {t("nav.contact")}
            </Link>
            <Link
              to="/about"
              className={navLinkClasses("/about")}
              onClick={() => setMenuOpen(false)}
            >
              <FaInfoCircle className="text-xs" />
              About
            </Link>
            <Link
              to="/order"
              className="bg-green-600 hover:bg-green-700 text-white font-semibold px-4 py-2 rounded-md shadow transition duration-200 text-center"
              onClick={() => setMenuOpen(false)}
            >
              🌍 Place Order
            </Link>
            <div className="flex justify-center gap-4 pt-2">
              <ThemeToggle />
              <LanguageSwitcher />
            </div>
          </nav>
      </div>
    </header>
  );
}
