import React, { useRef, useState } from "react";
import emailjs from "@emailjs/browser";
import { useTranslation } from "react-i18next";

export default function Contact() {
  const form = useRef();
  const { t } = useTranslation();
  const [preview, setPreview] = useState(null);

  const sendEmail = (e) => {
    e.preventDefault();
    emailjs
      .sendForm(
        process.env.REACT_APP_EMAILJS_SERVICE_ID,
        process.env.REACT_APP_EMAILJS_TEMPLATE_ID,
        form.current,
        process.env.REACT_APP_EMAILJS_PUBLIC_KEY
      )
      .then(
        (result) => {
          alert(t("contact.success"));
          form.current.reset();
          setPreview(null);
        },
        (error) => {
          alert(t("contact.error"));
          console.error(error.text);
        }
      );
  };

  return (
    <div className="max-w-2xl mx-auto p-6">
      <h2 className="text-3xl font-bold mb-6 text-center">
        {t("contact.title")}
      </h2>

      <form ref={form} onSubmit={sendEmail} className="space-y-4">
        <div>
          <label className="block font-medium">{t("contact.name")}</label>
          <input
            type="text"
            name="user_name"
            required
            className="w-full border rounded p-2"
          />
        </div>

        <div>
          <label className="block font-medium">{t("contact.email")}</label>
          <input
            type="email"
            name="user_email"
            required
            className="w-full border rounded p-2"
          />
        </div>

        <div>
          <label className="block font-medium">{t("contact.subject")}</label>
          <input
            type="text"
            name="subject"
            required
            className="w-full border rounded p-2"
          />
        </div>

        <div>
          <label className="block font-medium">{t("contact.category")}</label>
          <select name="category" className="w-full border rounded p-2">
            <option>{t("contact.options.oils")}</option>
            <option>{t("contact.options.soaps")}</option>
            <option>{t("contact.options.agro")}</option>
            <option>{t("contact.options.other")}</option>
          </select>
        </div>

        <div>
          <label className="block font-medium">{t("contact.message")}</label>
          <textarea
            name="message"
            rows="4"
            required
            className="w-full border rounded p-2"
          />
        </div>

        <div>
          <label className="block font-medium">{t("contact.attachment")}</label>
          <input
            type="file"
            name="attachment"
            accept="image/*"
            className="w-full border rounded p-2"
            onChange={(e) => {
              const file = e.target.files[0];
              if (file) setPreview(URL.createObjectURL(file));
            }}
          />
          {preview && (
            <img
              src={preview}
              alt="Preview"
              className="mt-2 h-32 object-cover rounded border"
            />
          )}
        </div>

        <button
          type="submit"
          className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
        >
          {t("contact.send")}
        </button>
      </form>
    </div>
  );
}
