import React from "react";
import { Link } from "react-router-dom";
import { useTranslation } from "react-i18next";
import LanguageSwitcher from "./ui/LanguageSwitcher"; // ✅ import reusable switcher

export default function Header() {
  const { t } = useTranslation();

  return (
    <header className="sticky top-0 z-50 flex items-center justify-between px-6 py-3 bg-white shadow-sm border-b border-gray-100">
      {/* Logo */}
      <Link
        to="/"
        className="text-2xl font-bold tracking-tight hover:opacity-90 transition-opacity"
      >
        <span className="text-green-600">Tropi</span>
        <span className="text-sky-500">Nord</span>
      </Link>

      {/* Navigation */}
      <nav className="flex gap-6 text-sm font-medium">
        <Link to="/" className="text-gray-700 hover:text-green-600 transition">
          {t("nav.home")}
        </Link>
        <Link
          to="/services"
          className="text-gray-700 hover:text-green-600 transition"
        >
          {t("nav.services")}
        </Link>
        <Link
          to="/explore"
          className="text-gray-700 hover:text-green-600 transition"
        >
          {t("nav.explore")}
        </Link>
        <Link
          to="/contact"
          className="text-gray-700 hover:text-green-600 transition"
        >
          {t("nav.contact")}
        </Link>
      </nav>

      {/* ✅ Reusable Language Switcher */}
      <div className="ml-6">
        <LanguageSwitcher />
      </div>
    </header>
  );
}
