import React, { useRef, useState } from "react";
import emailjs from "@emailjs/browser";
import { useTranslation } from "react-i18next";
import axios from "axios";
import { FaTrashAlt } from "react-icons/fa";

export default function Contact() {
  const form = useRef();
  const { t } = useTranslation();

  const [files, setFiles] = useState([]);
  const [uploadedUrls, setUploadedUrls] = useState([]);

  const cloudName = process.env.REACT_APP_CLOUDINARY_CLOUD_NAME;
  const uploadPreset = process.env.REACT_APP_CLOUDINARY_UPLOAD_PRESET;

  const handleFileChange = async (e) => {
    const selected = Array.from(e.target.files);
    setFiles((prev) => [...prev, ...selected]);

    for (let file of selected) {
      const formData = new FormData();
      formData.append("file", file);
      formData.append("upload_preset", uploadPreset);

      try {
        const res = await axios.post(
          `https://api.cloudinary.com/v1_1/${cloudName}/upload`,
          formData
        );
        setUploadedUrls((prev) => [...prev, res.data.secure_url]);
      } catch (err) {
        console.error("Upload failed:", err);
      }
    }
  };

  const removeFile = (index) => {
    setFiles((prev) => prev.filter((_, i) => i !== index));
    setUploadedUrls((prev) => prev.filter((_, i) => i !== index));
  };

  const sendEmail = (e) => {
    e.preventDefault();

    const formElement = form.current;
    const messageField = formElement.message;

    messageField.value +=
      "\n\nUploaded Files:\n" +
      uploadedUrls.map((url) => `- ${url}`).join("\n");

    emailjs
      .sendForm(
        process.env.REACT_APP_EMAILJS_SERVICE_ID,
        process.env.REACT_APP_EMAILJS_TEMPLATE_ID,
        form.current,
        process.env.REACT_APP_EMAILJS_PUBLIC_KEY
      )
      .then(
        (result) => {
          alert(t("contact.success"));
          form.current.reset();
          setFiles([]);
          setUploadedUrls([]);
        },
        (error) => {
          alert(t("contact.error"));
          console.error(error.text);
        }
      );
  };

  return (
    <div className="max-w-2xl mx-auto p-6">
      <h2 className="text-3xl font-bold mb-6 text-center">
        {t("contact.title")}
      </h2>

      <form ref={form} onSubmit={sendEmail} className="space-y-4">
        {/* ...your name/email/subject inputs... */}

        <div>
          <label className="block font-medium">Attach Files</label>
          <input
            type="file"
            accept=".jpg,.png,.pdf,.docx"
            multiple
            onChange={handleFileChange}
            className="w-full border rounded p-2"
          />
        </div>

        {files.length > 0 && (
          <div className="space-y-2 mt-2">
            {files.map((file, index) => (
              <div
                key={index}
                className="flex justify-between items-center bg-gray-100 px-3 py-2 rounded shadow"
              >
                <span className="truncate">{file.name}</span>
                <button
                  type="button"
                  onClick={() => removeFile(index)}
                  className="text-red-600 hover:text-red-800"
                  aria-label={`Remove ${file.name}`}
                >
                  <FaTrashAlt />
                </button>
              </div>
            ))}
          </div>
        )}

        <textarea name="message" hidden />

        <button
          type="submit"
          className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
        >
          {t("contact.send")}
        </button>
      </form>
    </div>
  );
}
