import React, { useState } from "react";
import { useTranslation } from "react-i18next";
import products from "../data/products";
import { Link } from "react-router-dom";

export default function Explore() {
  const { t } = useTranslation();
  const [searchTerm, setSearchTerm] = useState("");

  const filteredProducts = products.filter((product) =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="max-w-6xl mx-auto space-y-10 animate-fade-in-up">
      <header className="text-center">
        <h1 className="text-4xl font-bold text-green-800 mb-2">
          {t("explore.title")}
        </h1>
        <input
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          placeholder={t("explore.searchPlaceholder")}
          className="mt-4 px-4 py-2 rounded-md w-full sm:w-1/2 border border-gray-300 shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500"
        />
      </header>

      <section className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
        {filteredProducts.length > 0 ? (
          filteredProducts.map((product) => (
            <div
              key={product.slug}
              className="product-card hover:shadow-lg transition-all"
            >
              <h3 className="text-xl font-semibold">{t(`products.${product.slug}.name`)}</h3>
              <p className="text-sm text-gray-600 dark:text-gray-400">
                {t(`products.${product.slug}.description`)}
              </p>
              <ul className="mt-4 space-y-1">
                {product.features?.map((feature, index) => (
                  <li key={index}>{feature}</li>
                ))}
              </ul>
              <Link
                to={`/products/${product.slug}`}
                className="inline-block mt-4 bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-md"
              >
                {t("products.shopNow")}
              </Link>
            </div>
          ))
        ) : (
          <p className="col-span-full text-center text-gray-500">
            {t("products.notFound")}
          </p>
        )}
      </section>
    </div>
  );
}
