import React from "react";
import { Helmet } from "react-helmet";
import { useTranslation } from "react-i18next";
import {
  FaLeaf,
  FaSeedling,
  FaSpa,
  FaTractor,
  FaTruck,
  FaHandsHelping,
} from "react-icons/fa";

export default function Services() {
  const { t } = useTranslation();

  const services = [
    {
      icon: <FaLeaf className="text-green-600 text-3xl mb-2" />,
      title: "Organic Oils",
      description: "Cold-pressed oils such as coconut, almond, and neem."
    },
    {
      icon: <FaSpa className="text-green-600 text-3xl mb-2" />,
      title: "Natural Soaps",
      description: "Shea butter soaps, African black soaps, and more."
    },
    {
      icon: <FaTractor className="text-green-600 text-3xl mb-2" />,
      title: "Agro Imports",
      description: "Banana leaves, tools, charcoal, and organic farm produce."
    },
    {
      icon: <FaTruck className="text-green-600 text-3xl mb-2" />,
      title: "Distribution",
      description: "Efficient and ethical logistics across Sweden and Europe."
    },
    {
      icon: <FaSeedling className="text-green-600 text-3xl mb-2" />,
      title: "Eco-Sourcing",
      description: "Working directly with small producers for fair trade sourcing."
    },
    {
      icon: <FaHandsHelping className="text-green-600 text-3xl mb-2" />,
      title: "Community Outreach",
      description: "Youth wellness programs and sustainability education."
    },
  ];

  return (
    <div className="min-h-screen py-16 px-4 md:px-10 bg-white dark:bg-gray-900 text-gray-800 dark:text-gray-100">
      <Helmet>
        <title>Our Services | TropiNord</title>
        <meta
          name="description"
          content="Explore our wide range of wellness services and ethical products ‚Äî from organic oils to community-driven projects."
        />
      </Helmet>

      <div className="max-w-5xl mx-auto">
        <h1 className="text-4xl font-bold text-center text-green-700 dark:text-green-400 mb-12">
          üåç Our Services
        </h1>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-10">
          {services.map((service, index) => (
            <div
              key={index}
              className="bg-white dark:bg-gray-800 p-6 rounded-xl shadow-lg text-center hover:shadow-xl transition"
            >
              {service.icon}
              <h2 className="text-xl font-semibold mb-2 text-green-800 dark:text-green-200">
                {service.title}
              </h2>
              <p className="text-gray-600 dark:text-gray-300 text-sm">
                {service.description}
              </p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
