import React, { useState } from "react";
import jsPDF from "jspdf";
import "jspdf-autotable";

const productsList = [
  "Lavender Oil",
  "Lemon Oil",
  "Mint Essential Oil",
  "Avocado Oil",
  "Frankincense & Myrrh Essential Oil",
  "Native African Pear Oil",
];

const OrderForm = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    products: [],
    notes: "",
  });
  const [errors, setErrors] = useState({});
  const [previewURL, setPreviewURL] = useState("");

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    if (type === "checkbox") {
      const updated = checked
        ? [...formData.products, value]
        : formData.products.filter((p) => p !== value);
      setFormData((prev) => ({ ...prev, products: updated }));
    } else {
      setFormData((prev) => ({ ...prev, [name]: value }));
    }
  };

  const validate = () => {
    const newErrors = {};
    if (!formData.name) newErrors.name = "Name is required";
    if (!formData.email) newErrors.email = "Email is required";
    if (formData.products.length === 0) newErrors.products = "Select at least one product";
    if (!formData.notes) newErrors.notes = "Notes/quantity is required";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const generatePDF = () => {
    const doc = new jsPDF();
    doc.setFontSize(16);
    doc.text("TropiNord Order Preview", 20, 20);
    doc.setFontSize(12);
    doc.text(`Name: ${formData.name}`, 20, 40);
    doc.text(`Email: ${formData.email}`, 20, 50);
    doc.text(`Products: ${formData.products.join(", ")}`, 20, 60);
    doc.text(`Notes: ${formData.notes}`, 20, 70);
    const blob = doc.output("blob");
    const url = URL.createObjectURL(blob);
    setPreviewURL(url);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!validate()) return;

    const formURL =
      "https://docs.google.com/forms/d/e/1FAIpQLSePSc0eb42eMrU-KGKZiPbTtXGKmItQU6ds2Mw4KuuS-cFbdA/formResponse";
    const form = new FormData();
    form.append("entry.1234567890", formData.name);  // Replace with correct entry IDs
    form.append("entry.2345678901", formData.email);
    form.append("entry.3456789012", formData.products.join(", "));
    form.append("entry.4567890123", formData.notes);

    fetch(formURL, {
      method: "POST",
      mode: "no-cors",
      body: form,
    }).then(() => {
      alert("✅ Your order was submitted! You'll receive a confirmation email shortly.");
      setFormData({ name: "", email: "", products: [], notes: "" });
      setPreviewURL("");
    });
  };

  return (
    <div className="max-w-2xl mx-auto p-6 bg-white shadow rounded">
      <h2 className="text-2xl font-bold mb-4 text-green-700">Place Your Order</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block font-semibold">Name *</label>
          <input
            name="name"
            value={formData.name}
            onChange={handleChange}
            className="w-full border p-2 rounded"
          />
          {errors.name && <p className="text-red-600 text-sm">{errors.name}</p>}
        </div>
        <div>
          <label className="block font-semibold">Email *</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            className="w-full border p-2 rounded"
          />
          {errors.email && <p className="text-red-600 text-sm">{errors.email}</p>}
        </div>
        <div>
          <label className="block font-semibold">Select Products *</label>
          <div className="grid grid-cols-2 gap-2">
            {productsList.map((product) => (
              <label key={product} className="flex items-center">
                <input
                  type="checkbox"
                  value={product}
                  checked={formData.products.includes(product)}
                  onChange={handleChange}
                  className="mr-2"
                />
                {product}
              </label>
            ))}
          </div>
          {errors.products && <p className="text-red-600 text-sm">{errors.products}</p>}
        </div>
        <div>
          <label className="block font-semibold">Quantity / Notes *</label>
          <textarea
            name="notes"
            value={formData.notes}
            onChange={handleChange}
            className="w-full border p-2 rounded"
            rows="3"
          />
          {errors.notes && <p className="text-red-600 text-sm">{errors.notes}</p>}
        </div>
        <div className="flex gap-4">
          <button type="submit" className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700">
            Submit
          </button>
          <button type="button" onClick={generatePDF} className="border border-green-600 text-green-700 px-4 py-2 rounded hover:bg-gray-100">
            Preview PDF
          </button>
        </div>
      </form>
      {previewURL && (
        <div className="mt-6">
          <iframe src={previewURL} title="PDF Preview" width="100%" height="400" className="border rounded" />
        </div>
      )}
    </div>
  );
};

export default OrderForm;
