import React from "react";
import { Link } from "react-router-dom";
import { useTranslation } from "react-i18next";
import { FaInstagram, FaFacebook, FaYoutube } from "react-icons/fa";

const Footer = () => {
  const { t } = useTranslation();

  return (
    <footer className="bg-[#146b39] text-gold-200 py-8 px-4">
      <div className="max-w-7xl mx-auto">
        {/* Footer content */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {/* Navigation Links - Use Link instead of <a> */}
          <div>
            <h3 className="text-lg font-semibold mb-4">
              {t("footer.quickLinks")}
            </h3>
            <ul className="space-y-2">
              <li>
                <Link to="/about" className="hover:text-gold-100 transition">
                  {t("footer.about")}
                </Link>
              </li>
              <li>
                <Link to="/contact" className="hover:text-gold-100 transition">
                  {t("footer.contact")}
                </Link>
              </li>
              {/* Add more links as needed */}
            </ul>
          </div>

          {/* Social Media Links */}
          <div>
            <h3 className="text-lg font-semibold mb-4">
              {t("footer.followUs")}
            </h3>
            <div className="flex space-x-4">
              <a
                href="https://facebook.com"
                target="_blank"
                rel="noopener noreferrer"
              >
                <FaFacebook className="text-xl hover:text-gold-100 transition" />
              </a>
              <a
                href="https://instagram.com"
                target="_blank"
                rel="noopener noreferrer"
              >
                <FaInstagram className="text-xl hover:text-gold-100 transition" />
              </a>
              <a
                href="https://youtube.com"
                target="_blank"
                rel="noopener noreferrer"
              >
                <FaYoutube className="text-xl hover:text-gold-100 transition" />
              </a>
            </div>
          </div>
        </div>

        {/* Copyright */}
        <div className="mt-8 pt-6 border-t border-gold-700 text-center text-sm">
          Â© {new Date().getFullYear()} Trophford. {t("footer.rightsReserved")}
        </div>
      </div>
    </footer>
  );
};

export default Footer;
