import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import products from "../data/products";
import { useTranslation } from "react-i18next";

export default function ProductDetail() {
  const { slug } = useParams();
  const navigate = useNavigate();
  const { t } = useTranslation();

  const product = products.find((p) => p.slug === slug);
  const [selectedOptions, setSelectedOptions] = useState([]);
  const [cart, setCart] = useState([]);

  const toggleOption = (value) => {
    setSelectedOptions((prev) =>
      prev.includes(value)
        ? prev.filter((opt) => opt !== value)
        : [...prev, value]
    );
  };

  const addToCart = () => {
    const selectedItems = product.options.filter((opt) =>
      selectedOptions.includes(opt.value)
    );
    setCart([...cart, ...selectedItems]);
    alert("Items added to cart!");
  };

  if (!product) {
    return (
      <div className="p-6 text-center">
        <h2 className="text-xl font-semibold text-red-500">
          {t("products.notFound") || "Product not found."}
        </h2>
        <button
          onClick={() => navigate("/explore")}
          className="mt-4 px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700"
        >
          {t("explore.title") || "Back to Explore"}
        </button>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto p-6">
      {/* Image Gallery with Labels */}
      <div className="grid md:grid-cols-2 gap-4 mb-6">
        {(product.gallery || []).map((item, index) => (
          <div key={index} className="space-y-2">
            <img
              src={item.src || item}
              alt={item.name || `${product.name} ${index + 1}`}
              className="w-full h-64 object-cover rounded"
            />
            {item.name && (
              <div className="text-sm text-gray-700 font-medium">
                {item.name}
              </div>
            )}
            {item.description && (
              <div className="text-sm text-gray-500">
                {item.description}
              </div>
            )}
          </div>
        ))}
      </div>

      {/* Product Name & Description */}
      <h2 className="text-3xl font-bold mb-4">{product.name}</h2>
      <p className="text-lg text-gray-700 mb-6">{product.description}</p>

      {/* Option Checkboxes */}
      {product.options && (
        <div className="mb-6">
          <h3 className="text-xl font-semibold mb-2">Select your options:</h3>
          <div className="space-y-2">
            {product.options.map((option, index) => (
              <label key={index} className="flex items-center gap-2">
                <input
                  type="checkbox"
                  value={option.value}
                  checked={selectedOptions.includes(option.value)}
                  onChange={() => toggleOption(option.value)}
                />
                <span>{option.label}</span>
              </label>
            ))}
          </div>
        </div>
      )}

      {/* Add to Cart Button */}
      {product.options && (
        <button
          onClick={addToCart}
          className="bg-green-600 text-white px-6 py-2 rounded hover:bg-green-700 transition mr-4"
        >
          Add to Cart
        </button>
      )}

      {/* External Link */}
      {product.link && (
        <a
          href={product.link}
          target="_blank"
          rel="noopener noreferrer"
          className="inline-block bg-gray-200 text-green-700 px-6 py-2 rounded hover:bg-green-100 transition"
        >
          {t("products.shopNow") || "Shop Now"}
        </a>
      )}

      {/* Contact Button */}
      <button
        onClick={() => navigate("/contact")}
        className="bg-gray-100 text-green-700 px-6 py-2 rounded border border-green-600 hover:bg-green-50 transition"
      >
        {t("contact.title") || "Contact Us"}
      </button>
    </div>
  );
}
