import React, { useState, useEffect, Suspense } from "react";
import { BrowserRouter as Router, Routes, Route, useLocation } from "react-router-dom";

// Context
export const CartContext = React.createContext();

// Components
const Header = React.lazy(() => import("./components/ui/Header"));
const Footer = React.lazy(() => import("./components/ui/Footer"));
const ChatbotWidget = React.lazy(() => import("./components/ChatbotWidget"));
const WhatsAppChat = React.lazy(() => import("./components/ui/WhatsAppChat"));

// Pages
const Home = React.lazy(() => import("./pages/Home"));
const Services = React.lazy(() => import("./pages/Services"));
const Contact = React.lazy(() => import("./pages/Contact"));
const Explore = React.lazy(() => import("./pages/Explore"));
const ProductDetail = React.lazy(() => import("./pages/ProductDetail"));
const About = React.lazy(() => import("./pages/About"));
const OrderForm = React.lazy(() => import("./pages/OrderForm"));

const ScrollToTop = () => {
  const { pathname } = useLocation();
  useEffect(() => {
    window.scrollTo(0, 0);
  }, [pathname]);
  return null;
};

function App() {
  const [cartItems, setCartItems] = useState([]);
  const [orderInfo, setOrderInfo] = useState({ email: "", total: 0 });
  const excludedRoutes = ['/checkout', '/admin']; // Add routes where widgets shouldn't appear

  return (
    <CartContext.Provider value={{ cartItems, setCartItems, orderInfo, setOrderInfo }}>
      <Router>
        <ScrollToTop />
        <div className="flex flex-col min-h-screen">
          <Suspense fallback={<div>Loading...</div>}>
            <Header />
            <main className="flex-grow px-4 md:px-8 py-6">
              <Routes>
                <Route path="/" element={<Home />} />
                <Route path="/services" element={<Services />} />
                <Route path="/contact" element={<Contact />} />
                <Route path="/explore" element={<Explore />} />
                <Route path="/order" element={<OrderForm />} />
                <Route path="/products/:slug" element={<ProductDetail />} />
                <Route path="/about" element={<About />} />
              </Routes>
            </main>
            {!excludedRoutes.includes(location.pathname) && (
              <>
                <ChatbotWidget />
                <WhatsAppChat />
              </>
            )}
            <Footer />
          </Suspense>
        </div>
      </Router>
    </CartContext.Provider>
  );
}

export default App;
