import React, { useRef } from 'react';
import emailjs from 'emailjs-com';

export default function Contact() {
  const form = useRef();

  const sendEmail = (e) => {
	e.preventDefault();
	emailjs.sendForm(
  	process.env.REACT_APP_EMAILJS_SERVICE_ID,
  	process.env.REACT_APP_EMAILJS_TEMPLATE_ID,
  	form.current,
  	process.env.REACT_APP_EMAILJS_USER_ID
	)
	.then(result => {
  	alert("Message sent successfully!");
  	form.current.reset();
	}, error => {
  	alert("Something went wrong. Please try again.");
  	console.error(error.text);
	});
  };

  return (
	<div className="max-w-2xl mx-auto p-6">
  	<h2 className="text-3xl font-bold mb-6 text-center">Contact Us</h2>
  	<form ref={form} onSubmit={sendEmail} className="space-y-4">
    	<div>
      	<label className="block font-medium">Name</label>
      	<input type="text" name="user_name" required className="w-full border rounded p-2" />
    	</div>
    	<div>
      	<label className="block font-medium">Email</label>
      	<input type="email" name="user_email" required className="w-full border rounded p-2" />
    	</div>
    	<div>
      	<label className="block font-medium">Subject</label>
      	<input type="text" name="subject" required className="w-full border rounded p-2" />
    	</div>
    	<div>
      	<label className="block font-medium">Service Category</label>
      	<select name="category" className="w-full border rounded p-2">
        	<option>Organic Oils</option>
        	<option>Natural Soaps</option>
        	<option>Agro Imports</option>
        	<option>Other</option>
      	</select>
    	</div>
    	<div>
      	<label className="block font-medium">Message</label>
      	<textarea name="message" rows="4" required className="w-full border rounded p-2" />
    	</div>
    	<button type="submit" className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700">
      	Send Message
    	</button>
  	</form>
	</div>
  );
}
