import React from "react";
import { Link, useLocation } from "react-router-dom";
import { useTranslation } from "react-i18next";
import LanguageSwitcher from "./LanguageSwitcher";
import ThemeToggle from "./ThemeToggle";

export default function Header() {
  const location = useLocation();
  const { t } = useTranslation();

  const navLinkClasses = (path) =>
    `px-3 py-1 rounded-md transition-all duration-300 ${
      location.pathname === path
        ? "bg-green-600 text-white"
        : "text-gray-700 dark:text-gray-200 hover:bg-green-100 dark:hover:bg-green-800"
    }`;

  return (
    <header className="sticky top-0 z-50 flex flex-wrap md:flex-nowrap items-center justify-between px-6 py-3 bg-white dark:bg-gray-900 shadow-sm border-b border-gray-100 dark:border-gray-800">
      {/* Logo as image */}
      <Link
        to="/"
        className="hover:opacity-90 transition-opacity flex items-center"
      >
        <img
          src="/images/tropinordlogo.png"
          alt="TropiNord Logo"
          className="h-16 w-auto object-contain drop-shadow-sm"
        />
      </Link>

      {/* Navigation */}
      <nav className="flex flex-wrap gap-4 md:gap-6 text-sm font-medium mt-4 md:mt-0">
        <Link to="/" className={navLinkClasses("/")}>
          {t("nav.home")}
        </Link>
        <Link to="/services" className={navLinkClasses("/services")}>
          {t("nav.services")}
        </Link>
        <Link to="/explore" className={navLinkClasses("/explore")}>
          {t("nav.explore")}
        </Link>
        <Link to="/contact" className={navLinkClasses("/contact")}>
          {t("nav.contact")}
        </Link>
        <Link to="/about" className={navLinkClasses("/about")}>
          About
        </Link>
      </nav>

      {/* Tools */}
      <div className="flex items-center gap-4 ml-auto md:ml-6 mt-4 md:mt-0">
        <ThemeToggle />
        <LanguageSwitcher />
      </div>
    </header>
  );
}
