import React from "react";
import { Link } from "react-router-dom";
import { useTranslation } from "react-i18next";
import { FaGlobe } from "react-icons/fa";

export default function Header() {
  const { i18n } = useTranslation();

  const availableLanguages = [
    { code: "en", label: "English" },
    { code: "sv", label: "Svenska" }
    // You can add more here later: { code: "fr", label: "Français" }
  ];

  const changeLanguage = (lng) => {
    i18n.changeLanguage(lng);
  };

  return (
    <header className="sticky top-0 z-50 flex items-center justify-between px-6 py-3 bg-white shadow-sm border-b border-gray-100">
      {/* Logo */}
      <Link
        to="/"
        className="text-2xl font-bold tracking-tight hover:opacity-90 transition-opacity"
      >
        <span className="text-green-600">Tropi</span>
        <span className="text-sky-500">Nord</span>
      </Link>

      {/* Nav Links */}
      <nav className="flex gap-6 text-sm font-medium">
        <Link to="/" className="text-gray-700 hover:text-green-600 transition">
          Home
        </Link>
        <Link
          to="/services"
          className="text-gray-700 hover:text-green-600 transition"
        >
          Services
        </Link>
        <Link
          to="/explore"
          className="text-gray-700 hover:text-green-600 transition"
        >
          Explore
        </Link>
        <Link
          to="/contact"
          className="text-gray-700 hover:text-green-600 transition"
        >
          Contact
        </Link>
      </nav>

      {/* Language Switcher */}
      <div className="relative group ml-6 text-sm">
        <button className="flex items-center gap-1 text-gray-700 hover:text-green-700">
          <FaGlobe />
          <span>{i18n.language.toUpperCase()}</span>
        </button>
        <div className="absolute right-0 mt-2 bg-white border border-gray-200 rounded shadow-md hidden group-hover:block z-50">
          {availableLanguages.map((lang) => (
            <button
              key={lang.code}
              onClick={() => changeLanguage(lang.code)}
              className={`block w-full text-left px-4 py-2 hover:bg-green-50 ${
                i18n.language === lang.code ? "font-semibold text-green-700" : ""
              }`}
            >
              {lang.label}
            </button>
          ))}
        </div>
      </div>
    </header>
  );
}
