import React, { useState, useRef, useEffect } from "react";
import { Link } from "react-router-dom";
import { useTranslation } from "react-i18next";
import { FaGlobe } from "react-icons/fa";

export default function Header() {
  const { t, i18n } = useTranslation();
  const [showDropdown, setShowDropdown] = useState(false);
  const dropdownRef = useRef();

  const availableLanguages = [
    { code: "en", label: "English", flag: "ðŸ‡¬ðŸ‡§" },
    { code: "sv", label: "Svenska", flag: "ðŸ‡¸ðŸ‡ª" },
    { code: "ar", label: "Ø§Ù„Ø¹Ø±Ø¨ÙŠØ©", flag: "ðŸ‡¸ðŸ‡¦" },
    { code: "zh", label: "ä¸­æ–‡", flag: "ðŸ‡¨ðŸ‡³" },
    { code: "sw", label: "Kiswahili", flag: "ðŸ‡°ðŸ‡ª" },
    { code: "pt", label: "PortuguÃªs", flag: "ðŸ‡µðŸ‡¹" },
    { code: "es", label: "EspaÃ±ol", flag: "ðŸ‡ªðŸ‡¸" },
    { code: "fr", label: "FranÃ§ais", flag: "ðŸ‡«ðŸ‡·" },
    { code: "de", label: "Deutsch", flag: "ðŸ‡©ðŸ‡ª" },
    { code: "hi", label: "à¤¹à¤¿à¤¨à¥à¤¦à¥€", flag: "ðŸ‡®ðŸ‡³" }
  ];

  const toggleDropdown = () => {
    setShowDropdown((prev) => !prev);
  };

  const changeLanguage = (lng) => {
    i18n.changeLanguage(lng);
    localStorage.setItem("lng", lng);
    setShowDropdown(false);
  };

  // Close on click outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setShowDropdown(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  return (
    <header className="sticky top-0 z-50 flex items-center justify-between px-6 py-3 bg-white shadow-sm border-b border-gray-100">
      {/* Logo */}
      <Link
        to="/"
        className="text-2xl font-bold tracking-tight hover:opacity-90 transition-opacity"
      >
        <span className="text-green-600">Tropi</span>
        <span className="text-sky-500">Nord</span>
      </Link>

      {/* Navigation */}
      <nav className="flex gap-6 text-sm font-medium">
        <Link to="/" className="text-gray-700 hover:text-green-600 transition">
          {t("nav.home")}
        </Link>
        <Link to="/services" className="text-gray-700 hover:text-green-600 transition">
          {t("nav.services")}
        </Link>
        <Link to="/explore" className="text-gray-700 hover:text-green-600 transition">
          {t("nav.explore")}
        </Link>
        <Link to="/contact" className="text-gray-700 hover:text-green-600 transition">
          {t("nav.contact")}
        </Link>
      </nav>

      {/* Language Selector - Click-Based */}
      <div className="relative ml-6 text-sm" ref={dropdownRef}>
        <button
          onClick={toggleDropdown}
          className="flex items-center gap-1 text-gray-700 hover:text-green-700"
        >
          <FaGlobe />
          <span>{i18n.language.toUpperCase()}</span>
        </button>

        {showDropdown && (
          <div className="absolute right-0 mt-2 bg-white border border-gray-200 rounded shadow-md z-50">
            {availableLanguages.map((lang) => (
              <button
                key={lang.code}
                onClick={() => changeLanguage(lang.code)}
                className={`flex items-center w-full px-4 py-2 text-left hover:bg-green-50 ${
                  i18n.language === lang.code ? "font-semibold text-green-700" : ""
                }`}
              >
                <span className="mr-2">{lang.flag}</span>
                {lang.label}
              </button>
            ))}
          </div>
        )}
      </div>
    </header>
  );
}
